import FormInput from "../../components/form-in/form-in.component";
import Button from "../../components/button/button.component";

import { useState, useEffect } from "react";

import { 
   signInWithGooglePopup, 
   createUserDocumentFromAuth 
} from "../../utils/firebase/firebase.utils";


const defaultFormFields = {
  email: '',
  password: '',
};


const SignInForm = () => {
const [formFields, setFormFields ] = useState(defaultFormFields);
const { email, password } = formFields;

const resetFormFields = () => {
    setFormFields(defaultFormFields);
}
   const signInWithGoogle = async () => {
    const { user } = await signInWithGooglePopup();
    await createUserDocumentFromAuth(user);
   }

   //fetch user from firebase database
 /*  useEffect(
   fetch()
   .then(response => response.json)
   .then((user) =>{
      console.log(user);
   })
  ); */

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      //if user
     //check if user.email and user.password equal formFields
      resetFormFields();
    } catch (error) {

    }
    console.log('submit')
  }

  const handleChange = (event) => {
   const { name, value} = event.target;
   setFormFields({...formFields, [name]: value });
  }



   return (
     <div>
        <h1>I already have an account</h1>
        <span>Sign in with your email and password</span>
        <form onSubmit={handleSubmit}>
         <FormInput 
          label="Email"
          type="email"
          required

          onChange={handleChange}
          name="email" 
          value={email}
          />
          <FormInput 
          label="Password"
          type="password"
          required
          onChange={handleChange}
          name="password" 
          value={password}
          />
        <Button buttonType='default' type='submit'>Sign in</Button>
        <Button buttonType="google" type="button" onClick={signInWithGoogle}>Sign in with Google Popup</Button> 
        </form>
     </div>
   )
}


export default SignInForm;
