import FormInput from "../../components/form-in/form-in.component";
import Button from "../../components/button/button.component";

import { useState } from "react";

import { 
   signInWithGooglePopup, 
   createUserDocumentFromAuth 
} from "../../utils/firebase/firebase.utils";

import SignUpForm from '../../components/sign-up-form/sign-up.component';


const defaultFormFields = {
  email: '',
  password: '',
};

const SignIn = () => {

   const logGoogleUser = async () => {
      const { user } = await signInWithGooglePopup();
      const userDocRef = await createUserDocumentFromAuth(user);
      //console.log(user);
   }

const [formFields, setFormFields ] = useState(defaultFormFields);
const { email, password } = formFields;

  const handleChange = (event) => {
   const { name, value} = event.target;
   setFormFields({...formFields, [name]: value });
  }

  const handleSubmit = (event) => {
   event.preventDefault();
    console.log('submit')
  }
   return (
     <div>
        <h1>I already have an account</h1>
        <span>Sign in with your email and password</span>
        <form onSubmit={handleSubmit}>
         <FormInput 
          label="email"
          type="email"
          required
          onChange={handleChange}
          name="email" 
          value={email}
          />
          <FormInput 
          label="Password"
          type="password"
          required
          onChange={handleChange}
          name="password" 
          value={password}
          />

        </form>
        <Button buttonType="default" type="submit">Sign in</Button>
        {/* <Button buttonType="google" type="button" onClick={logGoogleUser}>Sign in with Google Popup</Button> */}
        <SignUpForm />
     </div>
   )
}


export default SignIn;
